query:
CREATE TABLE IF NOT EXISTS `reminders` (
	`id` int AUTO_INCREMENT NOT NULL,
	`odId` varchar(64) NOT NULL,
	`clientId` int NOT NULL,
	`pianoId` int,
	`reminderType` enum('call', 'visit', 'email', 'whatsapp', 'follow_up') NOT NULL,
	`dueDate` timestamp NOT NULL,
	`title` varchar(255) NOT NULL,
	`notes` text,
	`isCompleted` tinyint NOT NULL DEFAULT 0,
	`completedAt` timestamp NULL,
	`createdAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
	`updatedAt` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
	`partnerId` int NOT NULL DEFAULT 1,
	`organizationId` int,
	CONSTRAINT `reminders_id` PRIMARY KEY(`id`)
);

CREATE INDEX IF NOT EXISTS `idx_reminders_client` ON `reminders` (`clientId`);
CREATE INDEX IF NOT EXISTS `idx_reminders_piano` ON `reminders` (`pianoId`);
CREATE INDEX IF NOT EXISTS `idx_reminders_due_date` ON `reminders` (`dueDate`);
CREATE INDEX IF NOT EXISTS `idx_reminders_completed` ON `reminders` (`isCompleted`);
CREATE INDEX IF NOT EXISTS `idx_reminders_type` ON `reminders` (`reminderType`);
CREATE INDEX IF NOT EXISTS `idx_reminders_partner` ON `reminders` (`partnerId`);

